import { z } from 'zod'
import {insertAiSentences} from '~/server/repositories/sentence/sentences.repository'

const aiSentenceBodySchema = z.object({
  sentence: z.string(), 
  sentence_id: z.number(),
})

/**
 * @openapi
 *
 * /ai/sentence:
 *   post:
 *     security:
 *       - HeaderAuth: []
 *
 *     summary: Create a new AI sentence
 *     description: Create a sentence that is generated by the AI
 *
 *     requestBody:
 *       required: true
 *       content:
 *         application/json:
 *           schema:
 *             type: object
 *             properties:
 *               sentence:
 *                 type: string
 *                 format: string
 *               sentence_id:
 *                 type: integer
 *                 format: int
 *           example:
 *             sentence: "This is a sentence"
 *             sentence_id: 1
 *
 *     responses:
 *       '200':
 *         description: The sentence was created successfully
 *         content:
 *           application/json:
 *             schema:
 *               $ref: '#/components/schemas/Ok'
 *       '400':
 *         description: The request body was invalid
 *         content:
 *           application/json:
 *             schema:
 *               $ref: '#/components/schemas/ValidationIssues'
 *       '401':
 *         description: Unauthorized
 *         content:
 *           application/json:
 *             schema:
 *               $ref: '#/components/schemas/Unauthorized'
 *       '500':
 *         description: An internal server error occurred
 *         content:
 *           application/json:
 *             schema:
 *               $ref: '#/components/schemas/InternalServerError'
 */

export default defineEventHandler(async (event) => {
  const result = await readValidatedBody(event, (body) => aiSentenceBodySchema.safeParse(body))

  if (!result.success) {
    throw createError({
      message: 'Invalid body',
      data: result.error.issues,
      statusCode: 400,
    })
  }

  const body = result.data

  const user = event.context.user
  if (!user) {
    throw createError({
      message: 'Unauthorized',
      statusCode: 401,
    })
  }

  const data: any = {
    sentence: body.sentence,
    sentence_id: body.sentence_id,
    user_id: user.id,
  }

  try {
    const res = await insertAiSentences(db, data)
    return res.id
  } catch (error) {
    throw createError({
      message: 'An internal server error occurred',
      data: error,
      statusCode: 500,
    })
  }
})
